mapper.select:
select  RESOURCEID
		,RESOURCETYPE
		,RESOURCENAME
		,RESOURCEPRICE
		,RESOURCEBUYDATE
		,RESOURCEUSEYEAR
		,RESOURCESTATUS
		,RESOURCEINFOS
		,DUTYPERSON

   from resourcemsg


	and  RESOURCEID=#{resourceid}
	and  RESOURCETYPE=#{resourcetype}
	and  RESOURCENAME=#{resourcename}
	and  RESOURCEPRICE=#{resourceprice}
	and  RESOURCEBUYDATE=#{resourcebuydate}
	and  RESOURCEUSEYEAR=#{resourceuseyear}
	and  RESOURCESTATUS=#{resourcestatus}
	and  RESOURCEINFOS=#{resourceinfos}
	and  DUTYPERSON=#{dutyperson}

select *
 from resourcemsg


	and  RESOURCEID=#{resourceid}
	and  RESOURCETYPE=#{resourcetype}
	and  RESOURCENAME=#{resourcename}
	and  RESOURCEPRICE=#{resourceprice}
	and  RESOURCEBUYDATE=#{resourcebuydate}
	and  RESOURCEUSEYEAR=#{resourceuseyear}
	and  RESOURCESTATUS=#{resourcestatus}
	and  RESOURCEINFOS=#{resourceinfos}
	and  DUTYPERSON=#{dutyperson}

	select count(*)
      from resourcemsg 


		and  RESOURCEID=#{resourceid}
		and  RESOURCETYPE=#{resourcetype}
		and  RESOURCENAME=#{resourcename}
		and  RESOURCEPRICE=#{resourceprice}
		and  RESOURCEBUYDATE=#{resourcebuydate}
		and  RESOURCEUSEYEAR=#{resourceuseyear}
		and  RESOURCESTATUS=#{resourcestatus}
		and  RESOURCEINFOS=#{resourceinfos}
		and  DUTYPERSON=#{dutyperson}

mapper.update:
update resourcemsg
RESOURCETYPE=#{resourcetype}, RESOURCENAME=#{resourcename}, RESOURCEPRICE=#{resourceprice}, RESOURCEBUYDATE=#{resourcebuydate}, RESOURCEUSEYEAR=#{resourceuseyear}, RESOURCESTATUS=#{resourcestatus}, RESOURCEINFOS=#{resourceinfos}, DUTYPERSON=#{dutyperson},
RESOURCEID=#{resourceid}

mapper.delete:
delete from resourcemsg


		 and RESOURCEID=#{resourceid} 
		 and RESOURCETYPE=#{resourcetype} 
		 and RESOURCENAME=#{resourcename} 
		 and RESOURCEPRICE=#{resourceprice} 
		 and RESOURCEBUYDATE=#{resourcebuydate} 
		 and RESOURCEUSEYEAR=#{resourceuseyear} 
		 and RESOURCESTATUS=#{resourcestatus} 
		 and RESOURCEINFOS=#{resourceinfos} 
		 and DUTYPERSON=#{dutyperson}
